<?phpnamespace app\common\model;use app\common\model\Model;class MallOrderGoods extends Model{	protected $name = 'mall_order_goods';	protected $pk = 'og_id';	protected $insert = array('create_time');	protected $update = array('update_time');	public function getOrderGoods($order_id = 0){		$result = array();		if($order_id){			$result = $this->where(['order_id' => $order_id])->select();		}		return $result;	}}